type DateType = {
  /* day: ['월', ,'수'] */
  days: string[];
  /* time: ['시작시간', '종료시간'] */
  times: string[];
};

type SideNavProps = {
  type: string;
  title?: string[];
  student?: number;
  date?: DateType;
};

